mapper.select:
SELECT b.brand_code AS brandCode, b.brand_name AS brandName, b.category_code AS categoryCode, b.category_name AS categoryName, b.description FROM T_Brand b
AND ( b.brand_name LIKE CONCAT('%', #{keyword}, '%') OR b.category_name LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY b.brand_name ASC LIMIT #{size} OFFSET #{offset}
