mapper.select:
SELECT m.merchant_code AS merchantCode, m.merchant_name AS merchantName, m.business_id AS businessId, b.brand_code AS brandCode, b.brand_name AS brandName, c.category_code AS categoryCode, c.category_name AS categoryName, m.registration_date AS registrationDate, m.termination_date AS terminationDate, m.contract_status AS contractStatus FROM T_Merchant m LEFT JOIN T_Brand b ON m.brand_code = b.brand_code LEFT JOIN T_Category c ON b.category_code = c.category_code
AND ( m.merchant_name LIKE CONCAT('%', #{keyword}, '%') OR m.merchant_code LIKE CONCAT('%', #{keyword}, '%') OR m.business_id LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY m.merchant_code LIMIT #{size} OFFSET #{offset}
